import React, { useState, useEffect } from 'react';
import './Comp.css';
import SuggestionCities from './SuggestionCities';

export default function Comparison() {
  const [placeOne, setPlaceOne] = useState('');
  const [placeTwo, setPlaceTwo] = useState('');
  const [cities, setCities] = useState(['Madurai', 'Chennai']);
  const [filteredCitiesOne, setFilteredCitiesOne] = useState([]);

  useEffect(() => {
    let filteredCitiesOne;
    if (placeOne.length) {
      filteredCitiesOne = cities.filter(city =>
        city.toLocaleUpperCase().includes(placeOne.toLocaleUpperCase())
      );
    }
    setFilteredCitiesOne(filteredCitiesOne);
  }, [placeOne]);

  const changePlaceOne = event => {
    setPlaceOne(event.target.value);
  };
  return (
    <div>
      <header className="header">
        <h1>Weather Comparison</h1>
      </header>
      <div className="inputfield">
        <input
          className="place1"
          type="text"
          name="place 1"
          value={placeOne}
          list="citiesOne"
          placeholder="Enter your Place 1"
          onChange={event => {
            changePlaceOne(event);
          }}
        />
        {placeOne.length ? (
          <datalist id="citiesOne">
            {cities.map(city => (
              <option value={city} />
            ))}
          </datalist>
        ) : null}
        {/* <div className="suggesstions">
          {filteredCitiesOne && filteredCitiesOne.length
            ? filteredCitiesOne.map(city => <li>{city}</li>)
            : null}
        </div> */}
        <input
          className="place2"
          type="text"
          name="place 2"
          list="citiesTwo"
          value={placeTwo}
          placeholder="Enter your Place 2"
          onChange={event => setPlaceTwo(event.target.value)}
        />
        {placeTwo.length ? (
          <datalist id="citiesTwo">
            {cities.map(city => (
              <option value={city} />
            ))}
          </datalist>
        ) : null}
        <button type="submit" className="submit">
          Compare
        </button>
      </div>
    </div>
  );
}
