import React, { useState, useEffect } from 'react';
import './Comp.css';
import suggestedCities from './SuggestionCities.json';

export default function Comparison() {
  const [placeOne, setPlaceOne] = useState('');
  const [placeTwo, setPlaceTwo] = useState('');
  const [cities, setCities] = useState([]);
  // const [filteredCitiesOne, setFilteredCitiesOne] = useState([]);

  useEffect(() => {
    let tempCities = [];
    tempCities = suggestedCities.map(data => data.name);
    setCities(tempCities);
  }, []);

  return (
    <div>
      <form>
        <header className="header">
          <h1>Weather Comparison</h1>
        </header>
        <div className="inputfield">
          <input
            className="place1"
            type="text"
            name="place 1"
            value={placeOne}
            list="citiesOne"
            placeholder="Enter your Place 1"
            onChange={event => setPlaceOne(event.target.value)}
          />
          {placeOne.length ? (
            <datalist id="citiesOne">
              {cities.map(city => (
                <option value={city} />
              ))}
            </datalist>
          ) : null}
          <input
            className="place2"
            type="text"
            name="place 2"
            list="citiesTwo"
            value={placeTwo}
            placeholder="Enter your Place 2"
            onChange={event => setPlaceTwo(event.target.value)}
          />
          {placeTwo.length ? (
            <datalist id="citiesTwo">
              {cities.map(city => (
                <option value={city} />
              ))}
            </datalist>
          ) : null}
          <button type="submit" className="submit">
            Compare
          </button>
        </div>
      </form>
    </div>
  );
}
